import { useAuth } from "react-oidc-context";
import "./App.css";

function App() {
  const auth = useAuth();

  const handleFileUpload = async (event) => {
    const file = event.target.files[0];
    if (!file) return;

    const reader = new FileReader();

    reader.onload = async () => {
      try {
        const fileContent = reader.result;

        await fetch("https://m6i0iltykf.execute-api.ap-south-1.amazonaws.com/upload", {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
            Authorization: auth.user.id_token,
          },
          body: JSON.stringify({
            userId: auth.user.profile.sub,
            fileName: file.name,
            fileContent: fileContent,
          }),
        });

        alert("✅ File uploaded successfully!");
      } catch (err) {
        alert("❌ Upload failed");
        console.error(err);
      }
    };

    reader.readAsText(file);
  };

  // Loading state
  if (auth.isLoading) {
    return (
      <div className="container">
        <div className="card fade-in">
          <h2>Loading...</h2>
        </div>
      </div>
    );
  }

  // Error state
  if (auth.error) {
    return (
      <div className="container">
        <div className="card fade-in">
          <h2>Error</h2>
          <p>{auth.error.message}</p>
        </div>
      </div>
    );
  }

  // Logged in UI
  if (auth.isAuthenticated) {
    return (
      <div className="container">
        <div className="card slide-up">
          <h2>Welcome</h2>
          <p className="email">{auth.user?.profile.email}</p>

          <input
            type="file"
            onChange={handleFileUpload}
            style={{ margin: "20px 0" }}
          />

          <button className="btn logout" onClick={() => auth.removeUser()}>
            Logout
          </button>
        </div>
      </div>
    );
  }

  // Logged out UI
  return (
    <div className="container">
      <div className="card slide-up">
        <h2>Cloud File Vault</h2>
        <p>Secure document storage powered by AWS</p>
        <button className="btn login" onClick={() => auth.signinRedirect()}>
          Login with Cognito
        </button>
      </div>
    </div>
  );
}

export default App;
