import React, { useState } from "react";
import { useAuth } from "react-oidc-context";
import "./App.css";

function App() {
  const auth = useAuth();
  const [selectedFile, setSelectedFile] = useState(null);

  const handleUploadClick = () => {
    console.log("Upload button clicked");

    if (!selectedFile) {
      alert("Please choose a file first");
      return;
    }

    const reader = new FileReader();

    reader.onload = async () => {
      console.log("File read complete");

      try {
        const base64String = reader.result.split(",")[1];

        console.log("Sending request to API...");

        const response = await fetch(
          "https://m6i0iltykf.execute-api.ap-south-1.amazonaws.com/upload",
          {
            method: "POST",
            headers: {
              "Content-Type": "application/json",
              Authorization: auth.user.id_token,
            },
            body: JSON.stringify({
              userId: auth.user.profile.sub,
              fileName: selectedFile.name,
              fileContent: base64String,
            }),
          }
        );

        console.log("Response:", response);
        alert("✅ File uploaded successfully!");
      } catch (err) {
        console.error(err);
        alert("❌ Upload failed");
      }
    };

    reader.readAsDataURL(selectedFile);
  };

  if (auth.isAuthenticated) {
    return (
      <div className="container">
        <div className="card slide-up">
          <h2>Welcome</h2>
          <p className="email">{auth.user?.profile.email}</p>

          <input
            type="file"
            onChange={(e) => {
              console.log("File selected");
              setSelectedFile(e.target.files[0]);
            }}
            style={{ margin: "20px 0" }}
          />

          <button className="btn login" onClick={() => handleUploadClick()}>
            Upload File
          </button>

          <br /><br />

          <button className="btn logout" onClick={() => auth.removeUser()}>
            Logout
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="container">
      <div className="card slide-up">
        <h2>Cloud File Vault</h2>
        <button className="btn login" onClick={() => auth.signinRedirect()}>
          Login with Cognito
        </button>
      </div>
    </div>
  );
}

export default App;
